frappe.ui.form.on("Formula Template", {
    configurar: async function(frm) {
        try {
            frappe.show_progress('Carregando', 10, 100, 'Aguarde...');
            console.log('[Formula Template] Progress bar mostrado.');

            const response = await fetch('https://arteris-editor.meb.services/api/open-editor', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    uuid: frm.doc.name,
                    formula: frm.doc.formula
                })
            });

            frappe.hide_progress();
            console.log('[Formula Template] Progress bar escondido.');

            const contentType = response.headers.get('content-type');
            console.log('[Formula Template] Content-Type recebido:', contentType);

            if (contentType && contentType.includes('application/json')) {
                const resposta = await response.json();
                console.log('[Formula Template] Resposta JSON recebida:', resposta);

                if (resposta.success) {
                    const uuid = resposta.uuid; // Obtendo o UUID da resposta
                    const redirectUrl = `https://arteris-editor.meb.services/?uuid=${uuid}`; // Montando o URL de redirecionamento com o UUID
                    console.log('[Formula Template] Sucesso, abrindo nova aba:', redirectUrl);
                    window.open(redirectUrl, '_blank');
                } else {
                    console.warn('[Formula Template] Resposta sem sucesso:', resposta.message);
                    frappe.msgprint(resposta.message || 'Resposta sem sucesso.');
                }
            } else {
                const respostaTexto = await response.text();
                console.warn('[Formula Template] Resposta n√£o-JSON recebida:', respostaTexto);
                frappe.msgprint('Resposta n√£o JSON: ' + respostaTexto);
            }
        } catch (error) {
            frappe.hide_progress();
            console.error('[Formula Template] Erro no try/catch:', error);
            frappe.msgprint('Erro: ' + error);
        }
    }
});

